-- создал таблицу table1, в которой есть все product_id с разными product_name
SELECT
	product_id
INTO table1
FROM
(SELECT 
	DISTINCT o.product_id,
	o.product_name
FROM
	new_sample.new_sample_joined AS o
INNER JOIN 
	(SELECT 
		product_id,
		product_name
	FROM
	new_sample.new_sample_joined 
	GROUP BY product_id, product_name
	HAVING COUNT(product_id) > 1) AS a
ON o.product_id = a.product_id AND o.product_name <> a.product_name
ORDER BY 
	1) AS a;

-- далее извлек row_id, где product_id имеет несколько product_name
SELECT 
	row_id 
FROM 
	new_sample.new_sample_joined AS j
WHERE EXISTS
	(SELECT
		*
	FROM 	
		new_sample.table1 AS t
	WHERE 
		j.product_id = t.product_id);

